INSERT INTO `plant_encyclopedia` (`plant_id`, `plant_name`, `scientific_name`, `description`, `care_tips`, `harvest_time`, `watering_frequency`, `watering_time`, `watering_interval`, `origin_place`, `climate`, `fertilizer`, `uses`, `common_disease`, `harvest_time_days`) VALUES
('plant_00', 'Snake plant', 'Dracaena trifasciata', 'Snake Plant memiliki karakteristik daun panjang yang tumbuh tegak, berbentuk seperti pedang dengan ujung runcing. Daunnya keras, bertekstur, dan sering menampilkan pola garis-garis horizontal hijau muda atau abu-abu pada permukaan hijau tua, dengan beberapa varietas memiliki tepi berwarna kuning (variegata). Tanaman ini biasanya memiliki tinggi antara 30 cm hingga 120 cm, tergantung varietasnya, dengan lebar mencapai 20-40 cm yang dipengaruhi oleh jumlah daun. Snake Plant adalah tanaman yang tahan terhadap berbagai kondisi lingkungan, menjadikannya sangat cocok untuk dekorasi indoor maupun outdoor.', NULL, '6 bulan hingga 1 tahun untuk mencapai ukuran optimal (30-60 cm) dalam kondisi perawatan yang baik, tergantung lingkungan dan varietas.', '2 minggu sekali (musim panas), 3-4 minggu sekali (musim dingin).', NULL, 'Disiram setiap 2-3 minggu, tergantung kelembapan tanah dan suhu.', 'Afrika Barat', 'Iklim tropis dan subtropis; suhu ideal 15-30C', 'Pupuk cair atau pupuk slow-release berbasis nitrogen rendah, digunakan setiap 2-3 bulan selama musim tanam (musim semi dan panas)', 'Obat: Dalam pengobatan tradisional, digunakan untuk mengobati sakit kepala ringan dan sebagai pembersih udara alami. Hiasan: Tanaman hias yang populer untuk dekorasi rumah dan kantor karena toleransi rendah perawatan.', 'Root Rot (Busuk Akar): Penyebab: Overwatering atau tanah yang tidak memiliki drainase baik. Leaf Spot (Bercak Daun): Penyebab: Infeksi jamur atau bakteri akibat kelembapan berlebih. Mealybugs (Kutu Putih): Penyebab: Serangan hama yang mengisap getah tanaman.', NULL),
('plant_01', 'Ponytail Palm', 'Beaucarnea recurvata', 'Ponytail Palm memiliki batang tebal berbentuk seperti botol di pangkalnya, yang berfungsi menyimpan air. Daunnya panjang, melengkung, dan berbentuk seperti pita dengan ujung yang lembut, tumbuh membentuk roset menyerupai ekor kuda. Tinggi tanaman ini bervariasi, dapat mencapai 30 cm hingga 4 meter tergantung usia dan lingkungan, dengan lebar sekitar 30-60 cm. Warnanya hijau cerah, menambah kesan segar sebagai dekorasi.', NULL, '5-10 tahun tergantung kondisi pertumbuhan.', '2-4 minggu sekali (musim panas), 4-6 minggu (musim dingin).', NULL, 'Disiram setiap 2-4 minggu, tergantung kelembapan tanah.', 'Meksiko', 'Iklim hangat dan semi-kering, dengan suhu ideal 20-30C', 'Pupuk cair berbasis nitrogen rendah, diaplikasikan setiap 1-2 bulan selama musim tanam (musim semi dan panas).', 'Hiasan: Sangat populer sebagai tanaman hias, baik untuk indoor maupun outdoor, karena bentuknya yang unik dan mudah dirawat.', 'Root Rot: Disebabkan oleh overwatering. Leaf Tip Browning: Karena kekurangan air atau kelembapan rendah. Mealybugs dan Spider Mites: Hama umum yang menyerang daun.', NULL),
('plant_02', 'Polka Dot Plant', 'Hypoestes phyllostachya', 'Polka Dot Plant adalah tanaman hias berukuran kecil yang dikenal dengan pola bintik warna-warni pada daunnya. Daun berbentuk oval dengan warna dasar hijau dihiasi bintik merah muda, putih, atau merah yang mencolok. Tinggi tanaman ini sekitar 20–40 cm dengan lebar hingga 30 cm, menjadikannya tanaman dekorasi yang cantik untuk ruangan atau taman kecil.', NULL, '6-12 bulan setelah penanaman', '2-3 kali per minggu, 200 ml per penyiraman. Sesuaikan dengan kondisi tanah.', NULL, 'Disiram 2-3 kali per minggu dengan jumlah air sekitar 200 ml per penyiraman untuk tanaman berukuran 20-30 cm. Pastikan tanah tetap lembap tetapi tidak basah.', 'Madagaskar', 'Iklim Tropis dengan kelembapan tinggi, suhu ideal 18-30C', 'Pupuk cair organik rendah nitrogen, diaplikasikan setiap 2 minggu selama musim tanam.', 'Hiasan: Polka Dot Plant sangat cocok sebagai tanaman meja atau penghias taman kecil karena pola warna-warni daunnya.', 'Powdery Mildew: Infeksi jamur akibat kelembapan tinggi, menyebabkan lapisan putih pada daun. Aphids: Hama kecil yang menyerang daun muda, membuatnya keriting. Root Rot: Disebabkan oleh penyiraman berlebih sehingga akar membusuk.', NULL),
('plant_03', 'Monstera Deliciosa', 'Monstera Deliciosa', 'Monstera Deliciosa, juga dikenal sebagai \"Tanaman Keju Swiss,\" memiliki daun besar dengan lubang atau belahan khas yang memberikan tampilan tropis yang elegan. Daunnya berwarna hijau tua dan dapat mencapai ukuran 90 cm. Tanaman ini tumbuh hingga 3 meter di dalam ruangan jika diberi penyangga seperti tiang lumut. Selain keindahannya, Monstera juga dikenal sebagai tanaman pembersih udara alami.', NULL, '12 bulan untuk matang dan menghasilkan buah yang dapat dimakan.', '1 minggu sekali di musim panas dan 2 minggu sekali di musim dingin, dengan air secukupnya untuk membasahi seluruh tanah.', NULL, 'Disiram seminggu sekali dengan jumlah air yang cukup untuk membuat seluruh media tanam basah hingga ke dasar pot.', 'Amerika Tengah', 'Iklim tropis dengan suhu 20-30C dan kelembapan tinggi', 'Pupuk cair NPK, diaplikasikan sebulan sekali selama musim tanam (musim semi dan panas).', 'Hiasan: Monstera sangat populer sebagai tanaman indoor karena estetika tropisnya.', 'Root Rot: Disebabkan oleh drainase buruk atau penyiraman berlebih. Spider Mites: Hama kecil yang menyerang bagian bawah daun, menyebabkan bercak kuning. Leaf Spot: Infeksi jamur yang menyebabkan bercak coklat pada daun.', NULL),
('plant_04', 'Elephant Ear', 'Alocasia spp.', 'Elephant Ear adalah tanaman hias tropis dengan daun besar berbentuk hati yang menyerupai telinga gajah, sehingga menjadi pusat perhatian di taman atau ruangan. Daunnya biasanya berwarna hijau tua dengan tekstur yang halus atau sedikit bergelombang, dan beberapa varietas memiliki garis-garis kontras yang mencolok. Tanaman ini dapat mencapai tinggi 1–2 meter dengan lebar hingga 1 meter, tergantung spesiesnya.', NULL, '6–12 bulan untuk menghasilkan umbi yang dapat dimakan', '3–4 kali per minggu di musim panas dan 1–2 kali per minggu di musim dingin, dengan jumlah air yang cukup untuk menjaga kelembapan tanah.', NULL, 'Disiram 3–4 kali per minggu untuk menjaga tanah tetap lembap, terutama selama musim panas. Selama musim dingin, cukup 1–2 kali per minggu.', 'Asia Tenggara', 'Iklim tropis dan subtropis dengan suhu ideal 21–32C dan kelembapan tinggi.', 'Pupuk cair dengan kandungan nitrogen tinggi, diaplikasikan setiap 2 minggu selama musim tanam.', 'Hiasan: Daunnya yang besar dan unik menjadikannya tanaman hias favorit untuk area taman tropis atau ruang terbuka. Bahan makanan: Umbi dari beberapa spesies dapat diolah menjadi makanan tradisional.', 'Root Rot: Disebabkan oleh penyiraman berlebih atau drainase buruk. Spider Mites: Hama kecil yang menyerang bagian bawah daun, meninggalkan bercak kuning. Leaf Spot: Infeksi jamur yang menyebabkan bercak coklat pada daun.', NULL),
('plant_05', 'Dumb Cane', 'Dieffenbachia spp.', 'Dumb Cane adalah tanaman berdaun besar dengan pola unik berupa bercak putih, hijau muda, atau krem di daunnya. Tanaman ini biasanya tumbuh hingga setinggi 1-2 meter sebagai tanaman indoor. Lebar daun bisa mencapai 20-30 cm. Tumbuhan ini dikenal karena kemampuannya menyaring udara, tetapi getahnya beracun dan dapat menyebabkan iritasi jika tersentuh atau tertelan.', NULL, '60-90 hari untuk mencapai ukuran yang cukup besar dan menarik sebagai tanaman hias setelah ditanam dari stek atau bibit.', '7-10 hari sekali, tergantung pada kelembaban lingkungan dan musim.', NULL, 'Disiram ketika lapisan atas tanah mulai kering. Terlalu banyak air dapat menyebabkan busuk akar.', 'Amerika Tengah dan Selatan', 'Iklim tropis dan subtropis dengan suhu antara 18-24C', 'Pupuk cair serbaguna setiap 4-6 minggu selama musim tumbuh (musim semi dan musim panas).', 'Hiasan: Sangat populer sebagai tanaman dekorasi indoor karena perawatannya yang mudah dan penampilannya yang menarik.', 'Leaf spot: Disebabkan oleh infeksi jamur atau bakteri. Root rot: Akibat penyiraman berlebih atau drainase buruk. Pest infestations: Rentan terhadap kutu daun, tungau laba-laba, dan serangga skala.', NULL),
('plant_06', 'Dracaena', 'Dracaena spp.', 'Dracaena adalah tanaman hias dengan daun yang panjang, ramping, dan sering bergaris atau berwarna hijau tua dengan variasi putih, kuning, atau merah. Batangnya keras dan tegak, menyerupai batang pohon kecil. Tanaman ini tumbuh lambat dan bisa mencapai tinggi hingga 2-3 meter di dalam ruangan, dengan lebar sekitar 50-100 cm. Dracaena dikenal sebagai tanaman yang mudah dirawat dan mampu menyaring udara di dalam ruangan.', NULL, '90-120 hari diperlukan untuk mencapai ukuran cukup besar.', 'Setiap 7-14 hari tergantung kelembapan dan suhu.', NULL, 'Disiram setiap 1-2 minggu, pastikan air tidak tergenang di pot.', 'Afrika, Asia, dan Amerika Tengah', 'Iklim tropis dan subtropis dengan suhu antara 18-24C', 'Pupuk cair serbaguna atau pupuk khusus tanaman hias setiap 4-6 minggu selama musim semi dan musim panas.', 'Hiasan: Sangat populer sebagai tanaman dekorasi indoor karena perawatannya yang mudah dan penampilannya yang elegan.', 'Leaf spot: Disebabkan oleh infeksi jamur atau bakteri. Root rot: Akibat penyiraman berlebih atau drainase buruk. Brown tips: Disebabkan oleh kekurangan kelembaban atau air dengan kandungan fluoride tinggi.', NULL),
('plant_08', 'Birds Nest Fern', 'Asplenium nidus', 'Bird\'s Nest Fern adalah tanaman paku yang memiliki daun panjang, lebar, berwarna hijau terang dengan tekstur mengkilap. Daunnya tumbuh melingkar dari pusat seperti sarang burung, itulah asal namanya. Tanaman ini dapat tumbuh setinggi 60-120 cm dengan lebar 50-100 cm. Tanaman ini sangat estetis dan populer sebagai dekorasi karena keindahan daunnya yang unik dan mudah dirawat.', NULL, '90-120 hari untuk mencapai ukuran yang menarik sebagai dekorasi.', '7-14 hari, tergantung pada kelembaban tanah dan lingkungan.', NULL, 'Disiram ketika tanah mulai kering, tetapi jangan biarkan tanah terlalu kering. Frekuensi penyiraman biasanya setiap 1-2 minggu tergantung pada kelembaban lingkungan.', 'Asia Tenggara, Afrika, dan Australia', 'Iklim tropis dan subtropis dengan suhu ideal antara 18-27C. Membutuhkan kelembaban tinggi.', 'Pupuk cair untuk tanaman hijau atau tanaman hias setiap 6-8 minggu selama musim tumbuh.', 'Hiasan: Sering digunakan sebagai tanaman dekorasi indoor atau outdoor karena penampilannya yang estetis dan kemampuannya menambah kesan tropis.', 'Leaf spot: Disebabkan oleh infeksi jamur atau bakteri. Brown tips: Disebabkan oleh udara kering atau air dengan kandungan garam atau mineral tinggi. Pest infestations: Rentan terhadap tungau laba-laba dan kutu putih.', NULL),
('plant_09', 'Areca Palm', 'Dypsis lutescens', 'Areca Palm adalah tanaman hias dengan batang ramping menyerupai buluh dan daun panjang berbentuk melengkung yang terdiri dari banyak daun kecil berwarna hijau terang. Tumbuhan ini biasanya tumbuh setinggi 1,5–3 meter dalam ruangan dan bisa mencapai 6–12 meter di luar ruangan. Areca Palm sering digunakan untuk menciptakan nuansa tropis di rumah dan dikenal sebagai salah satu tanaman pembersih udara alami.', NULL, '90-180 hari sejak ditanam, tergantung pada kondisi perawatan.', '7-14 hari, tergantung pada kelembaban tanah dan lingkungan.', NULL, 'Disiram secara teratur, memastikan tanah tetap lembab tetapi tidak basah. Penyiraman biasanya diperlukan 1-2 kali seminggu, tergantung pada kelembaban lingkungan.', 'Madagaskar', 'Iklim tropis dan subtropis dengan suhu ideal antara 18-30C. Areca Palm membutuhkan kelembaban tinggi.', 'Pupuk khusus tanaman tropis atau pupuk cair serbaguna setiap 6-8 minggu selama musim semi dan musim panas.', 'Hiasan: Sering digunakan sebagai tanaman dekorasi indoor dan outdoor karena penampilannya yang tropis dan kemampuannya menyaring udara.', 'Leaf spot: Disebabkan oleh jamur atau bakteri. Root rot: Akibat penyiraman berlebihan atau drainase buruk. Brown tips: Disebabkan oleh kekurangan kelembaban atau air dengan kandungan fluoride atau klorin tinggi.', NULL),
('plant_10', 'Aloe Vera', 'Aloe barbadensis miller', 'Aloe Vera adalah tanaman sukulen dengan daun tebal berwarna hijau kebiruan, berbentuk segitiga, dan ujungnya berduri kecil. Tanaman ini dikenal karena getahnya yang kaya manfaat untuk kesehatan dan kecantikan. Tingginya dapat mencapai 30–60 cm, sementara lebarnya bervariasi tergantung kondisi pertumbuhan. Aloe Vera adalah tanaman perawatan rendah yang tumbuh baik di dalam maupun di luar ruangan.', NULL, '6-8 bulan sejak ditanam untuk gelnya.', '7–14 hari, tergantung pada kelembaban tanah dan suhu lingkungan.', NULL, 'Disiram biasanya 1-2 minggu sekali, tergantung kondisi cuaca.', 'Afrika Utara dan Timur Tengah', 'Iklim kering hingga semi-tropis dengan suhu antara 20–30C', 'Pupuk organik atau pupuk sukulen sekali setiap bulan selama musim semi dan musim panas.', 'Hiasan: Cocok untuk dekorasi indoor dan outdoor karena bentuknya yang unik. Obat: Digunakan sebagai bahan alami untuk perawatan kulit, luka bakar ringan, dan masalah pencernaan. Bahan makanan: Gel Aloe Vera sering dijadikan bahan untuk minuman atau makanan kesehatan.', 'Root rot: Disebabkan oleh penyiraman berlebih. Pest infestations: Rentan terhadap kutu putih dan tungau laba-laba. Soft rot: Akibat infeksi bakteri atau jamur.', 180),
('plant_11', 'Peach', 'Prunus persica', 'Peach adalah tanaman buah yang memiliki pohon kecil hingga sedang dengan daun hijau lonjong dan bunga berwarna merah muda atau putih. Buah persiknya berbentuk bulat dengan kulit berbulu halus, daging buah manis yang lembut, dan biji keras di bagian tengah. Tinggi pohonnya dapat mencapai 3-8 meter, dengan diameter tajuk sekitar 3-5 meter.', NULL, '90-150 hari setelah berbunga.', '1-2 kali seminggu, tergantung pada tingkat kelembaban tanah dan kondisi cuaca.', NULL, 'Disiram secara teratur, terutama selama masa pertumbuhan dan pembentukan buah. Tanah harus tetap lembab tetapi tidak tergenang air.', 'China', 'Suhu optimal antara 18-30C', 'Gunakan pupuk kaya nitrogen, fosfor, dan kalium. Pemupukan dilakukan setiap musim semi dan musim panas.', 'Hiasan: Bunga Peach yang cantik sering digunakan sebagai tanaman hias, terutama di musim semi. Obat: Buahnya kaya akan vitamin dan antioksidan yang baik untuk kesehatan. Bahan makanan: Dikonsumsi segar atau diolah menjadi makanan seperti pai, selai, atau jus.', 'Peach leaf curl: Disebabkan oleh jamur Taphrina deformans. Brown rot: Akibat infeksi jamur pada buah matang. Powdery mildew: Lapisan putih pada daun dan batang akibat infeksi jamur.', 730),
('plant_12', 'Grape', 'Vitis vinifera', 'Anggur adalah tanaman merambat dengan batang berkayu, daun berbentuk menjari, dan buah yang tumbuh dalam tandan. Buahnya kecil, bulat atau oval, dan tersedia dalam berbagai warna seperti hijau, merah, dan ungu. Tanaman ini dapat tumbuh hingga 10-15 meter jika tidak dipangkas, tetapi biasanya dipangkas untuk mempermudah perawatan dan panen.', NULL, '150-180 hari setelah bunga mekar.', '1-2 kali seminggu, tergantung pada kelembaban tanah dan iklim.', NULL, 'Disiram secara teratur, selama masa pertumbuhan awal dan pembentukan buah. Tanah harus tetap lembab tetapi tidak tergenang air.', 'Eropa dan Asia Barat', 'Suhu optimal antara 15-30C', 'Pupuk kaya fosfor dan kalium untuk mendukung pertumbuhan buah. Pemupukan dilakukan secara rutin selama musim tumbuh.', 'Hiasan: Tanaman anggur juga dapat digunakan untuk dekorasi di pergola atau dinding taman. Obat: Kaya akan antioksidan dan vitamin, anggur memiliki manfaat kesehatan seperti meningkatkan imunitas dan melindungi jantung. Bahan makanan: Digunakan untuk konsumsi langsung, jus, kismis, atau wine.', 'Powdery mildew: Jamur yang menyebabkan lapisan putih pada daun dan buah. Downy mildew: Infeksi jamur yang menyebabkan bintik kuning pada daun. Black rot: Jamur yang menyerang buah dan daun.', 730),
('plant_18', 'Blueberry', 'Vaccinium corymbosum', 'Blueberry adalah tanaman perdu kecil dengan tinggi sekitar 1–2 meter. Daunnya kecil, oval, berwarna hijau, dan akan berubah menjadi merah keunguan pada musim gugur. Buahnya kecil, bulat, dengan diameter sekitar 0,5–1,5 cm, berwarna biru tua hingga ungu dengan lapisan lilin yang memberikan tampilan buram.', NULL, '2–3 tahun setelah tanam dari bibit.', '2–3 kali per minggu dengan jumlah air sekitar 300–500 ml per penyiraman.', NULL, 'Disiram 2–3 kali per minggu dengan jumlah air sekitar 300–500 ml per penyiraman. Pastikan tanah tetap lembap tetapi tidak tergenang air.', 'Amerika Utara', 'Iklim subtropis hingga sedang dengan suhu ideal 15–25C.', 'Pupuk asam, seperti sulfur atau NPK dengan kandungan nitrogen tinggi, diaplikasikan setiap 4–6 minggu.', 'Bahan makanan: Kaya antioksidan dan digunakan dalam kue, jus, atau smoothies.', 'Mummy Berry: Disebabkan oleh jamur Monilinia vaccinii-corymbosi. Stem Blight: Akibat infeksi Botryosphaeria dothidea. Root Rot: Disebabkan oleh jamur Phytophthora.', 700),
('plant_19', 'Potato', 'Solanum tuberosum', 'Potato atau kentang adalah tanaman berdaun hijau berbentuk oval dengan bunga kecil berwarna putih hingga ungu. Umbi kentang tumbuh di bawah tanah, berbentuk bulat hingga oval dengan kulit halus atau kasar berwarna coklat, kuning, merah, atau ungu tergantung varietas. Tanaman ini tumbuh hingga tinggi 50–100 cm.', NULL, '70–120 hari setelah tanam tergantung varietas.', '2–3 kali per minggu dengan jumlah air sekitar 1 liter per penyiraman.', NULL, 'Disiram 2–3 kali per minggu dengan jumlah air sekitar 1 liter per penyiraman. Pastikan tanah tetap lembap tetapi tidak tergenang air, terutama saat umbi mulai terbentuk.', 'Pegunungan Andes, Amerika Selatan', 'Iklim tropis hingga sedang dengan suhu ideal 15–20C.', 'Pupuk organik atau NPK dengan kandungan kalium tinggi diaplikasikan setiap 2–3 minggu.', 'Bahan makanan: Sumber utama karbohidrat di banyak negara. Industri: Digunakan dalam produksi tepung kentang dan makanan olahan.', 'Late Blight: Disebabkan oleh jamur Phytophthora infestans. Blackleg: Akibat infeksi bakteri Pectobacterium atrosepticum. Common Scab: Disebabkan oleh bakteri Streptomyces scabies.', 80),
('plant_20', 'Raspberry', 'Rubus idaeus', 'Raspberry adalah tanaman perdu kecil dengan cabang yang berduri dan daun majemuk berbentuk oval bergerigi. Tanaman ini menghasilkan buah kecil yang terdiri dari banyak druplet, berwarna merah cerah, kuning, ungu, atau hitam, tergantung varietas. Buahnya memiliki tekstur lembut dengan rasa manis dan sedikit asam. Tinggi tanaman bisa mencapai 1,5–2 meter, dengan lebar sekitar 1 meter. Raspberry juga sering berbunga putih kecil yang mempercantik tanaman saat musim semi.', NULL, '1–2 tahun setelah tanam.', '2–3 kali per minggu dengan jumlah air sekitar 500 ml per penyiraman.', NULL, 'Disiram 2–3 kali per minggu dengan jumlah air sekitar 500 ml per penyiraman. Pastikan tanah tetap lembap terutama saat musim panas atau saat buah mulai matang.', 'Eropa dan Asia Utara', 'Iklim subtropis hingga sedang, dengan suhu ideal 16–25C.', 'Pupuk organik kaya nitrogen atau pupuk NPK seimbang diaplikasikan setiap bulan selama musim tanam.', 'Bahan makanan: Dimakan segar, dijadikan jus, selai, atau bahan kue. Kesehatan: Kaya akan vitamin C dan antioksidan, membantu meningkatkan sistem imun.', 'Raspberry Cane Blight: Disebabkan oleh jamur Leptosphaeria coniothyrium. Root Rot: Akibat jamur Phytophthora fragariae. Powdery Mildew: Infeksi jamur yang menyebabkan lapisan putih pada daun.', 730),
('plant_21', 'Apple', 'Malus domestica', 'Apple atau apel adalah pohon buah yang berukuran sedang dengan daun hijau berbentuk oval bergigi halus. Bunganya kecil berwarna putih dengan semburat merah muda, tumbuh berkelompok. Buah apel berbentuk bulat dengan kulit halus, tersedia dalam berbagai warna seperti merah, hijau, atau kuning, tergantung varietas. Apel memiliki daging buah yang renyah dengan rasa manis hingga asam. Tinggi pohon apel berkisar antara 4–10 meter, dengan lebar mahkota sekitar 3–6 meter. Tanaman ini dikenal karena masa tanam yang panjang dan kemampuan adaptasinya yang baik.', NULL, '3–5 tahun setelah tanam dari bibit.', '2–3 kali per minggu dengan jumlah air sekitar 3–5 liter per penyiraman.', NULL, 'Disiram 2–3 kali per minggu dengan jumlah air sekitar 3–5 liter per penyiraman untuk pohon dewasa. Tanah harus lembap tetapi tidak terlalu basah.', 'Asia', 'Iklim subtropis hingga sedang, dengan suhu ideal 18–24C.', 'Pupuk organik atau NPK dengan kandungan kalium tinggi diaplikasikan setiap bulan selama musim tanam.', 'Bahan makanan: Dikonsumsi langsung atau digunakan dalam pembuatan jus, pai, dan saus. Kesehatan: Kaya akan serat dan vitamin C, baik untuk pencernaan dan kesehatan kulit.', 'Apple Scab: Disebabkan oleh jamur Venturia inaequalis. Fire Blight: Infeksi bakteri Erwinia amylovora. Powdery Mildew: Infeksi jamur pada daun dan bunga.', 1300),
('plant_22', 'Tomato', 'Solanum lycopersicum', 'Tomato atau tomat adalah tanaman semak dengan daun majemuk berbentuk oval dan bergerigi. Tanaman ini menghasilkan buah kecil hingga sedang berbentuk bulat atau lonjong, tersedia dalam warna merah, kuning, hijau, dan oranye. Tomat memiliki daging buah yang berair dengan rasa segar dan manis sedikit asam. Tinggi tanaman tomat berkisar antara 60 cm hingga 2 meter, tergantung varietas dan teknik perawatan. Bunganya kecil, berwarna kuning cerah, tumbuh dalam kelompok.', NULL, '60–80 hari setelah tanam dari bibit.', '2–3 kali per minggu dengan jumlah air sekitar 1 liter per penyiraman.', NULL, 'Disiram 2–3 kali per minggu dengan jumlah air sekitar 1 liter per penyiraman untuk tanaman berukuran 30–50 cm. Hindari penyiraman langsung pada daun untuk mencegah penyakit.', 'Amerika Selatan', 'Iklim tropis hingga sedang, dengan suhu ideal 20–30C.', 'Pupuk organik atau NPK dengan kandungan fosfor dan kalium tinggi diaplikasikan setiap 2–3 minggu.', 'Bahan makanan: Digunakan dalam salad, saus, sup, dan jus. Kesehatan: Kaya akan likopen, antioksidan yang baik untuk kesehatan jantung dan kulit.', 'Blossom End Rot: Disebabkan oleh kekurangan kalsium. Early Blight: Akibat jamur Alternaria solani. Powdery Mildew: Infeksi jamur pada daun.', 65);

